<?php

namespace WPTE_PRODUCT_LAYOUT\Includes;

/**
 * Plugin Installation Class
 *
 * @since 1.0.0
 */
class Installation {

	/**
	 * Installation class constructor
	 *
	 * Database
	 *
	 * @since 1.0.0
	 */
	public function __construct() {
		$this->activate();
		$this->product_layout_db();

		$is_data_exists = wpte_get_product_layouts();

		if ( ! $is_data_exists ) {
			$this->insert_first_shortcode();
		}
		
	}

	/**
	 * After activate Plugin
	 *
	 * @since 1.0.0
	 */
	public function activate() {

		$installed = get_option( 'WPTE_wpl_installed' );

		if ( ! $installed ) {

			update_option( 'WPTE_wpl_installed', time() );
		}

		update_option( 'WPTE_wpl_version', WPTE_WPL_VERSION );

		add_option( 'WPTE_wpl_activation_redirect', true );
	}

	/**
	 * Product Layout DB
	 *
	 * @since 1.0.0
	 */
	public function product_layout_db() {
		global $wpdb;
		$table_name      = $wpdb->prefix . 'wpte_product_layout_style';
		$charset_collate = $wpdb->get_charset_collate();

		$wpte_sql = "CREATE TABLE $table_name (
				id mediumint(5) NOT NULL AUTO_INCREMENT,
				name varchar(50) NOT NULL,
				style_name varchar(40) NOT NULL,
				rawdata longtext,
				stylesheet longtext,
				font_family text,
				PRIMARY KEY  (id)
		) $charset_collate";

		require_once ABSPATH . 'wp-admin/includes/upgrade.php';
		dbDelta( $wpte_sql );
	}

